import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
   
    constructor(){
        super();
        this.state={
            articles:this.articles,
            loading:false
        }
      }

     async componentDidMount(){
        let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=b44fd5c883844615a793393db08d2a97";
        let data= await fetch(url);
        let parsedata= await data.json();
        this.setState({articles:parsedata.articles})
      }
  render() {
    return (
      <div className='container mx-5 my-3'>
       <h2>Top Headlines</h2>
       <div className="row">
        {this.state.articles.map((element)=>{
              return  <div className="col-md-4" key={element.url}>
              <NewsItem  tittle={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} imageurl={element.urlToImage} newsurl={element.url}/>
              </div>
        })}
       
       
       </div>
       
      
      </div>
    )
  }
}

export default News
